import axios from 'axios';
import {
  parseHeaderForLinks,
  loadMoreDataWhenScrolled,
  ICrudGetAction,
  ICrudGetAllAction,
  ICrudPutAction,
  ICrudDeleteAction
} from 'react-jhipster';

import { cleanEntity } from 'app/shared/util/entity-utils';
import { REQUEST, SUCCESS, FAILURE } from 'app/shared/reducers/action-type.util';

import { IUserAdditional, defaultValue } from 'app/shared/model/user-additional.model';

export const ACTION_TYPES = {
  FETCH_USERADDITIONAL_LIST: 'userAdditional/FETCH_USERADDITIONAL_LIST',
  FETCH_USERADDITIONAL: 'userAdditional/FETCH_USERADDITIONAL',
  CREATE_USERADDITIONAL: 'userAdditional/CREATE_USERADDITIONAL',
  UPDATE_USERADDITIONAL: 'userAdditional/UPDATE_USERADDITIONAL',
  DELETE_USERADDITIONAL: 'userAdditional/DELETE_USERADDITIONAL',
  RESET: 'userAdditional/RESET'
};

const initialState = {
  loading: false,
  errorMessage: null,
  entities: [] as ReadonlyArray<IUserAdditional>,
  entity: defaultValue,
  links: { next: 0 },
  updating: false,
  totalItems: 0,
  updateSuccess: false
};

export type UserAdditionalState = Readonly<typeof initialState>;

// Reducer

export default (state: UserAdditionalState = initialState, action): UserAdditionalState => {
  switch (action.type) {
    case REQUEST(ACTION_TYPES.FETCH_USERADDITIONAL_LIST):
    case REQUEST(ACTION_TYPES.FETCH_USERADDITIONAL):
      return {
        ...state,
        errorMessage: null,
        updateSuccess: false,
        loading: true
      };
    case REQUEST(ACTION_TYPES.CREATE_USERADDITIONAL):
    case REQUEST(ACTION_TYPES.UPDATE_USERADDITIONAL):
    case REQUEST(ACTION_TYPES.DELETE_USERADDITIONAL):
      return {
        ...state,
        errorMessage: null,
        updateSuccess: false,
        updating: true
      };
    case FAILURE(ACTION_TYPES.FETCH_USERADDITIONAL_LIST):
    case FAILURE(ACTION_TYPES.FETCH_USERADDITIONAL):
    case FAILURE(ACTION_TYPES.CREATE_USERADDITIONAL):
    case FAILURE(ACTION_TYPES.UPDATE_USERADDITIONAL):
    case FAILURE(ACTION_TYPES.DELETE_USERADDITIONAL):
      return {
        ...state,
        loading: false,
        updating: false,
        updateSuccess: false,
        errorMessage: action.payload
      };
    case SUCCESS(ACTION_TYPES.FETCH_USERADDITIONAL_LIST):
      const links = parseHeaderForLinks(action.payload.headers.link);
      return {
        ...state,
        links,
        loading: false,
        totalItems: action.payload.headers['x-total-count'],
        entities: loadMoreDataWhenScrolled(state.entities, action.payload.data, links)
      };
    case SUCCESS(ACTION_TYPES.FETCH_USERADDITIONAL):
      return {
        ...state,
        loading: false,
        entity: action.payload.data
      };
    case SUCCESS(ACTION_TYPES.CREATE_USERADDITIONAL):
    case SUCCESS(ACTION_TYPES.UPDATE_USERADDITIONAL):
      return {
        ...state,
        updating: false,
        updateSuccess: true,
        entity: action.payload.data
      };
    case SUCCESS(ACTION_TYPES.DELETE_USERADDITIONAL):
      return {
        ...state,
        updating: false,
        updateSuccess: true,
        entity: {}
      };
    case ACTION_TYPES.RESET:
      return {
        ...initialState
      };
    default:
      return state;
  }
};

const apiUrl = 'api/user-additionals';

// Actions

export const getEntities: ICrudGetAllAction<IUserAdditional> = (page, size, sort) => {
  const requestUrl = `${apiUrl}${sort ? `?page=${page}&size=${size}&sort=${sort}` : ''}`;
  return {
    type: ACTION_TYPES.FETCH_USERADDITIONAL_LIST,
    payload: axios.get<IUserAdditional>(`${requestUrl}${sort ? '&' : '?'}cacheBuster=${new Date().getTime()}`)
  };
};

export const getEntity: ICrudGetAction<IUserAdditional> = id => {
  const requestUrl = `${apiUrl}/${id}`;
  return {
    type: ACTION_TYPES.FETCH_USERADDITIONAL,
    payload: axios.get<IUserAdditional>(requestUrl)
  };
};

export const createEntity: ICrudPutAction<IUserAdditional> = entity => async dispatch => {
  const result = await dispatch({
    type: ACTION_TYPES.CREATE_USERADDITIONAL,
    payload: axios.post(apiUrl, cleanEntity(entity))
  });
  return result;
};

export const updateEntity: ICrudPutAction<IUserAdditional> = entity => async dispatch => {
  const result = await dispatch({
    type: ACTION_TYPES.UPDATE_USERADDITIONAL,
    payload: axios.put(apiUrl, cleanEntity(entity))
  });
  return result;
};

export const deleteEntity: ICrudDeleteAction<IUserAdditional> = id => async dispatch => {
  const requestUrl = `${apiUrl}/${id}`;
  const result = await dispatch({
    type: ACTION_TYPES.DELETE_USERADDITIONAL,
    payload: axios.delete(requestUrl)
  });
  return result;
};

export const reset = () => ({
  type: ACTION_TYPES.RESET
});
